CREATE TABLE customer
(
  fb text PRIMARY KEY,
  displayName VARCHAR(100),
  email VARCHAR(100),
  phone varchar(22)
);


CREATE TABLE drivers
(
  id SERIAL PRIMARY KEY,
  firstname VARCHAR(40),
  lastname VARCHAR(40),
  ridetype VARCHAR(40),
  make VARCHAR(40),
  model VARCHAR(40),
  year INTEGER,
  licence VARCHAR(10),
  color VARCHAR(40),
  img TEXT
);

CREATE TABLE rides
(
  rideId SERIAL PRIMARY KEY,
  originlat decimal,
  originlong decimal,
  origindate VARCHAR(50),
  destlat decimal,
  destlong decimal,
  status VARCHAR(40),
  destdate VARCHAR(50),
  rating INT,
  review TEXT
);

alter table rides add column driver_id integer references drivers(id);
alter table rides add column facebook_id text references customer(fb);

